.body-container {
    margin: 0 auto;
    max-width: 1200px;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 20px;
    background-color: #F5F5F5;
}

.ul {
    display: flex;
    justify-content: space-between;
    list-style-type: none;
    padding: 0;
    margin: 0;
}

.li {
    margin: 0 20px;
    display: inline;
    font-size: 24px;
    color: #28CB8B;
    transition: color 0.3s;
    cursor: pointer;
}

.h1 {
    display: flex;
    background-color: #ABBED1;
    padding: 76px;
    text-align: center;
    font-size: 64px;
    text-transform: uppercase;
}

.h2 {
    display: flex;
    -color: #28CB8B;
    padding: 76px;
    text-align: center;
    font-size: 48px;
}

.navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #ABBED1;
    padding: 24px;
    font-size: 18px;
    flex-wrap: wrap;
}

.navbar-nav {
    display: flex;
    list-style-type: none;
    padding: 0;
    margin: 0;
}

.image-1 {
    box-shadow: 8px, #ABBED1;
    width: 75%;
    height: 25%;
    display: block;
    margin: 20px auto;
    border-radius: 50%;
    position: absolute;
}

.button {
    background-color: #28CB8B;
    padding: 10px;
    text-align: center;
    font-size: 18px;
    border-radius: 50%;
}

.p {
    margin: 20px;
    font-size: 18px;
    line-height: 1.5;
    text-align: justify;
    font-weight: 400;
    border-top: 1px solid #ABBED1;
    border-bottom: 1px solid #ABBED1;
    padding-top: 20px;
    padding-bottom: 20px;
}

.footer {
    background-color: #263238;
    color: #fff;
    padding: 10px;
    text-align: center;
    justify-items: center;

}
